import React from 'react'
import { useRef, useState, useEffect } from 'react';
/**/
const Login = () => {
    const userRef = useRef(); /**/
    const errRef = errRef();/*error*/

    const [user , setUser] = useState('');
    const [pwd , setPwd] = useState('');

    const [errMsg , setErrMsg] = useState(''); /*Shows if there is any error*/
    const[success, setSuccess] = useState(false); /*shows when you have successfully loged in*/


    useEffect(() => {
        useRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg(''); /*erases previous errors  */
    }, [user,pwd])

  return (
    <section>
        <p ref={errRef} className={errMsg ? "errmsg": "offscreen"} aria-live="assertive">{errMsg}</p>/*Shows error Msg */
        <h1>Sign In</h1>
        <form>
            <label htmlFor='username'>UserName: </label>
            <input type='text' 
            id="username" 
            ref={userRef} 
            autoComplete='off' 
            onChange={(e) => setUser(e.target.value)}
            value={user}
            required
            />
            <label htmlFor='password'>Password: </label>
            <input type='password' 
            id="password" 
            ref={userRef} 
            autoComplete='off' 
            onChange={(e) => setUser(e.target.value)}
            value={user}
            required
            />


        </form>
    </section>
  )
}

export default Login
