import React, { useState } from 'react';

const heartRateData = {
  20: { min: 100, max: 170 },
  30: { min: 95, max: 162 },
  35: { min: 93, max: 157 },
  40: { min: 90, max: 153 },
  45: { min: 88, max: 149 },
  50: { min: 85, max: 145 },
  55: { min: 100, max: 170 },
  60: { min: 95, max: 162 },
  65: { min: 93, max: 157 },
  70: { min: 75, max: 128 },
  75: { min: 73, max: 123 },
  80: { min: 70, max: 119 },
};

const bloodPressureData = {
  '18-39': {
    female: { systolic: 100, diastolic: 68 },
    male: { systolic: 119, diastolic: 70 },
  },
  '40-59': {
    female: { systolic: 122, diastolic: 74 },
    male: { systolic: 124, diastolic: 77 },
  },
  '60+': {
    female: { systolic: 139, diastolic: 68 },
    male: { systolic: 133, diastolic: 69 },
  },
};

function ProfilePage() {
  const [formData, setFormData] = useState({
    age: '',
    heartRate: '',
    bloodPressure: '',
    bloodOxygen: '',
    gender: 'female',
  });

  const [result, setResult] = useState('');

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { age, heartRate, bloodPressure, bloodOxygen, gender } = formData;

    const ageNum = parseInt(age);
    const heartRateNum = parseInt(heartRate);
    const bloodOxygenNum = parseInt(bloodOxygen);
    const [systolicBP, diastolicBP] = bloodPressure.split('/').map(Number);

    if (isNaN(systolicBP) || isNaN(diastolicBP)) {
      setResult("Please enter a valid blood pressure in the format systolic/diastolic.");
      return;
    }

    if (!heartRateData[ageNum]) {
      setResult("Age not recognized. Please enter a valid age.");
      return;
    }

    const { min, max } = heartRateData[ageNum];
    let heartRateMessage = heartRateNum >= min && heartRateNum <= max
      ? "Your heart rate is good!"
      : "Your heart rate is not good.";

    let ageCategory;
    if (ageNum >= 18 && ageNum <= 39) {
      ageCategory = '18-39';
    } else if (ageNum >= 40 && ageNum <= 59) {
      ageCategory = '40-59';
    } else if (ageNum >= 60) {
      ageCategory = '60+';
    } else {
      setResult("Age not recognized. Please enter a valid age.");
      return;
    }

    const { systolic, diastolic } = bloodPressureData[ageCategory][gender];
    let bloodPressureMessage = (systolicBP <= systolic && diastolicBP <= diastolic)
      ? "Your blood pressure is good!"
      : "Your blood pressure is not good.";

    let bloodOxygenMessage = "";
    if (bloodOxygenNum >= 95 && bloodOxygenNum <= 100) {
      bloodOxygenMessage = "Your blood oxygen level is normal.";
    } else if (bloodOxygenNum >= 80 && bloodOxygenNum < 95) {
      bloodOxygenMessage = "Your blood oxygen level is abnormal; the brain may be affected.";
    } else if (bloodOxygenNum < 67) {
      bloodOxygenMessage = "Cyanosis: Blood oxygen level is critically low.";
    } else {
      bloodOxygenMessage = "Your blood oxygen level is not good.";
    }

    setResult(
      `<div class="result good">${heartRateMessage}</div>
      <div class="result ${bloodPressureMessage.includes("not good") ? "bad" : "good"}">${bloodPressureMessage}</div>
      <div class="result ${bloodOxygenMessage.includes("not good") ? "bad" : "good"}">${bloodOxygenMessage}</div>`
    );
  };

  return (
    <div>
      <h1>Profile Page</h1>
      <form id="healthForm" onSubmit={handleSubmit}>
        <label htmlFor="age">Enter your age:</label>
        <input type="number" id="age" value={formData.age} onChange={handleChange} required />
        <br /><br />
        <label htmlFor="heartRate">Enter your heart rate (bpm):</label>
        <input type="number" id="heartRate" value={formData.heartRate} onChange={handleChange} required />
        <br /><br />
        <label htmlFor="bloodPressure">Enter your blood pressure (systolic/diastolic mm Hg):</label>
        <input type="text" id="bloodPressure" value={formData.bloodPressure} onChange={handleChange} placeholder="e.g., 120/80" required />
        <br /><br />
        <label htmlFor="bloodOxygen">Enter your blood oxygen level (%):</label>
        <input type="number" id="bloodOxygen" value={formData.bloodOxygen} onChange={handleChange} required />
        <br /><br />
        <label htmlFor="gender">Select your gender:</label>
        <select id="gender" value={formData.gender} onChange={handleChange} required>
          <option value="female">Female</option>
          <option value="male">Male</option>
        </select>
        <br /><br />
        <button type="submit">Check</button>
      </form>
      <div id="result" className="result" dangerouslySetInnerHTML={{ __html: result }}></div>
    </div>
  );
}

export default ProfilePage;
