import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

function AccountPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [showPopup, setShowPopup] = useState(false);
  const [logoutMessage, setLogoutMessage] = useState("");
  const navigate = useNavigate();

  const handleLogout = () => {
    setLogoutMessage("You are logged out");
    setShowPopup(true); // Show the logout message popup
    setTimeout(() => {
      setShowPopup(false); // Hide popup after 2 seconds
      navigate("/login"); // Redirect to login page after logout
    }, 2000); // Popup will disappear after 2 seconds
    setIsLoggedIn(false); // Log out the user
  };

  const handleDeleteAccount = () => {
    setShowPopup(true); // Show popup for account deletion message
    setTimeout(() => {
      setShowPopup(false); // Hide popup after 2 seconds
      setIsLoggedIn(false); // Log out the user after account deletion
      navigate("/register"); // Redirect to register page after deletion
    }, 2000); // Display the popup for 2 seconds
  };

  return (
    <Container>
      {isLoggedIn ? (
        <>
          <UserSection>
            <p>UserName: DAVE</p>
            <Button onClick={handleLogout}>LogOut</Button>
            <Button onClick={handleDeleteAccount}>Delete Account</Button>
          </UserSection>
          {showPopup && <Popup>{logoutMessage}</Popup>}
        </>
      ) : (
        <p>You are logged out</p>
      )}
    </Container>
  );
}

// Styled Components
const Container = styled.div`
  padding: 20px;
`;

const UserSection = styled.div`
  margin-bottom: 20px;
`;

const Button = styled.button`
  margin: 10px;
  padding: 10px;
  cursor: pointer;

  &:hover {
    background-color: #ddd;
  }
`;

const Popup = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: rgba(0, 0, 0, 0.8);
  color: white;
  padding: 20px;
  border-radius: 5px;
  font-size: 16px;
  z-index: 1000;
  text-align: center;
`;

export default AccountPage;
