import { openDB } from 'idb';
import bcrypt from 'bcryptjs'; // Ensure bcrypt is correctly installed and imported

// Open the IndexedDB
const openDatabase = async () => {
  try {
    const db = await openDB('myDatabase', 1, (upgradeDb) => {
      if (!upgradeDb.objectStoreNames.contains('users')) {
        const objectStore = upgradeDb.createObjectStore('users', { keyPath: 'username' });
        objectStore.createIndex('username', 'username', { unique: true });
      }
    });
    return db;
  } catch (error) {
    console.error("Error opening database:", error);
    throw error; // Re-throw the error for handling in the calling function
  }
};

// Register the user with password hashing
async function registerUser(username, password) {
    try {
      console.log("Attempting to open database...");
  
      const db = await openDB('myDatabase', 1, {
        upgrade(db) {
          if (!db.objectStoreNames.contains('users')) {
            console.log("Creating object store...");
            const objectStore = db.createObjectStore('users', { keyPath: 'username' });
            objectStore.createIndex('username', 'username', { unique: true });
          } else {
            console.log("Object store already exists.");
          }
        },
      });
  
      console.log("Database opened successfully:", db);
  
      const tx = db.transaction('users', 'readwrite');
      const store = tx.objectStore('users');
      await store.add({ username, password });
  
      await tx.done;  // Make sure the transaction completes before logging success
  
      console.log("User registered successfully:", username);
    } catch (error) {
      console.error("Error registering user:", error);
      throw error; // Re-throw the error so it can be handled in the calling function
    }
  }
  

// Function to hash the password using bcrypt
function hashPassword(password) {
  const salt = bcrypt.genSaltSync(10);
  return bcrypt.hashSync(password, salt); // Hash the password with bcrypt
}

// Function to login the user by verifying password
export const loginUser = async (username, password) => {
    try {
      const db = await openDatabase();
      const tx = db.transaction('users', 'readonly');
      const store = tx.objectStore('users');
      const user = await store.get(username); // Get the user by username
  
      if (user && verifyPassword(password, user.password)) {
        console.log("Login successful!");
        return true; // Successful login
      } else {
        console.log("Invalid credentials");
        return false; // Invalid credentials
      }
    } catch (error) {
      console.error("Error logging in:", error);
      throw error;
    }
  };
  
  // Function to verify the password using bcrypt
  function verifyPassword(password, hashedPassword) {
    return bcrypt.compareSync(password, hashedPassword); // Compare the entered password with the stored hashed password
  }
