// Register.js

import React, { useRef, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { registerUser } from '../API/db.js';

const USER_REGEX = /^[A-Za-z][A-Za-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

const Register = () => {
  const navigate = useNavigate();
  
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState('');
  const [validName, setValidName] = useState(false);
  const [userFocus, setUserFocus] = useState(false);

  const [pwd, setPwd] = useState('');
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);

  const [matchPwd, setMatchPwd] = useState('');
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false);

  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    const result = USER_REGEX.test(user);
    setValidName(result);
  }, [user]);

  useEffect(() => {
    const result = PWD_REGEX.test(pwd);
    setValidPwd(result);
    const match = pwd === matchPwd;
    setValidMatch(match);
  }, [pwd, matchPwd]);

  useEffect(() => {
    setErrMsg('');
  }, [user, pwd, matchPwd]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate inputs
    if (!USER_REGEX.test(user) || !PWD_REGEX.test(pwd) || !validMatch) {
      setErrMsg("Invalid Entry");
      return;
    }

    try {
      // Register user in IndexedDB
      await registerUser(user, pwd);
      setUser('');
      setPwd('');
      setMatchPwd('');
      setSuccess(true);
      navigate('/home');
    } catch (error) {
      if (error.name === 'ConstraintError') {
        setErrMsg("Username already exists. Please choose a different one.");
      } else {
        setErrMsg("An error occurred during registration. Please try again later.");
      }
    }
  };

  return (
    <section>
      {success ? (
        <h1>Success! You are now registered.</h1>
      ) : (
        <>
          <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
          <h1>Register</h1>
          <form onSubmit={handleSubmit}>
            <label htmlFor="username">
              Username:
              <span className={validName ? "valid" : "hide"}>✔</span>
              <span className={!validName && user ? "invalid" : "hide"}>×</span>
            </label>
            <input
              type="text"
              id="username"
              ref={userRef}
              autoComplete="off"
              onChange={(e) => setUser(e.target.value)}
              required
              aria-invalid={validName ? "false" : "true"}
              onFocus={() => setUserFocus(true)}
              onBlur={() => setUserFocus(false)}
            />
            <p className={userFocus && !validName ? "instructions" : "offscreen"}>
              4 to 24 characters. Must begin with a letter. Letters, numbers, underscores, hyphens allowed.
            </p>

            <label htmlFor="password">
              Password:
              <span className={validPwd ? "valid" : "hide"}>✔</span>
              <span className={!validPwd && pwd ? "invalid" : "hide"}>×</span>
            </label>
            <input
              type="password"
              id="password"
              onChange={(e) => setPwd(e.target.value)}
              required
              aria-invalid={validPwd ? "false" : "true"}
              onFocus={() => setPwdFocus(true)}
              onBlur={() => setPwdFocus(false)}
            />
            <p className={pwdFocus && !validPwd ? "instructions" : "offscreen"}>
              8 to 24 characters. Must include uppercase, lowercase letters, a number, and a special character.
            </p>

            <label htmlFor="confirm_pwd">
              Confirm Password:
              <span className={validMatch && matchPwd ? "valid" : "hide"}>✔</span>
              <span className={!validMatch && matchPwd ? "invalid" : "hide"}>×</span>
            </label>
            <input
              type="password"
              id="confirm_pwd"
              onChange={(e) => setMatchPwd(e.target.value)}
              required
              aria-invalid={validMatch ? "false" : "true"}
              onFocus={() => setMatchFocus(true)}
              onBlur={() => setMatchFocus(false)}
            />
            <p className={matchFocus && !validMatch ? "instructions" : "offscreen"}>
              Must match the first password input field.
            </p>

            <button disabled={!validName || !validPwd || !validMatch}>Sign Up</button>
          </form>
          <p>
            Already registered? <a href="/login">Sign in</a>
          </p>
        </>
      )}
    </section>
  );
};

export default Register;
